DCPDF = {};

/**
 * @summary Show an error message in the progress gui for pdf-generation.
 * @param errorText "Alarm alarm"
 * @param isInProgress Boolean, if you want to show the error-message during the process or in the choosing phase.
 */
DCPDF.showError = function (errorText, isInProgress) {
    var $alert = $('.export_alert');
    if ($alert.length < 1) {
        return;
    }

    $alert.text(errorText);
};

// TODO add options dynamically
DCGlobals = {};
DCGlobals.getWork = function () {
    return {
        structure: {
            pages_length: 0,
        },
        title: $('#pdf_title').text().trim(),
        author: $('#pdf_author').text().trim(),
        yearPublish: $('#pdf_year-publish').text().trim(),
        purl: $('#pdf_purl').text().trim(),
        license: $('#pdf_license').text().trim()
    };
};

/**
 * @summary Resets the Dialog and calculates the begin and start of the selected Section.
 */
DCPDF.reset = function () {
    DCPDF.isRunning = false;

    // Cancel loading of images if in progress
    $.each(DCPDF.images, function(index, image) {
        image.src = '';
    });

    $('#pdf-options, .export_cancel').show();
    $('#pdf-progress, .export_reset').hide();
};

/**
 * @summary Switches to progress view after choosing options.
 */
DCPDF.showProgress = function () {
    $('#pdf-options, #pdf-progress').toggle();
};

/**
 * @summary Set progress state for the progressbar.
 * @param progressVal
 */
DCPDF.setProgress = function (progressVal) {
    var $progressBar = $(".export_progress");
    var percentage = Math.floor(progressVal / DCPDF.maxValueForProgress * 100);
    $progressBar
        .css("width", percentage + "%")
        .data("value", progressVal)
        .html(percentage + "&nbsp;%");
};

/**
 * @summary Return the progress of the progressbar.
 * @returns {Number}
 */
DCPDF.getProgress = function () {
    return $(".export_progress").data("value");
};

/**
 * @summary Return a new promise for adding it later to an array of promises. Images will be loaded and returned as data-url.
 * @param path URL of image
 * @param physId
 */
DCPDF.preloadImage = function (path, physId) {
    return new Promise(function (resolve, reject) {
        // Create a new image from JavaScript
        var image = new Image();
        image.setAttribute('crossOrigin', 'anonymous');

        // Bind an event listener on the load to call the `resolve` function
        // FROM http://stackoverflow.com/questions/934012/get-image-data-in-javascript
        image.onload = function () {
            var canvas = document.createElement('canvas');
            canvas.width = this.width;
            canvas.height = this.height;
            var context = canvas.getContext('2d');
            context.drawImage(this, 0, 0);
            var dataURL = canvas.toDataURL('image/jpeg');
            var item = {
                dataURL: dataURL,
                width: this.width,
                height: this.height
            };
            DCPDF.base64data[physId] = item;
            DCPDF.setProgress(DCPDF.getProgress() + 1);
            resolve();
        };
        // If the image fails to be downloaded, we don't want the whole system
        // to collapse so we `resolve` instead of `reject`, even on error
        image.onerror = resolve;
        // Apply the path as `src` to the image so that the browser fetches it
        image.src = path;
        DCPDF.images.push(image);
    });
};

/**
 * @summary Handles the loading process for images in the pdf and adds them after successful loading into the pdf.
 * @param ppn Like PPN123456789
 */
DCPDF.preload = function (ppn) {
    // Promises are not supported, let's leave
    if (! ('Promise' in window)) {
        return;
    }
    DCPDF.base64data = [];
    // Replace the image path with a promise that will resolve when the image
    // gets downloaded by the browser.
    $.each(DCPDF.base64images, function (key, src) {
        if (typeof src !== undefined) {
            DCPDF.base64images[key] = DCPDF.preloadImage(src + "full/full/0/default.jpg", key);
            DCPDF.imagesInfos[key] = DCPDF.loadMetadata(src + "info.json");
        }
    });

    // When all images have been fetched...
    Promise.all( DCPDF.base64images.concat(DCPDF.imagesInfos) ).then(function () {
        if (! DCPDF.isRunning) {
            return false;
        }
        DCPDF.setProgress(DCPDF.maxValueForProgress);
        DCPDF.buildPDF(ppn);
    }).catch(function (err) {
        DCPDF.showError('pdf_error_general');
        console.log(err);
    });
};

/**
 * @summary Load IIIF metdata for image (info.json)
 * @param ppn Like PPN123456789
 */
DCPDF.loadMetadata = function (src) {
    return Promise.resolve($.getJSON(src));
}

/**
 * Build an array with urls assembled from the given physIds. Need a list of physIds to assemble the right ngcs(Content Server) url.
 * @param physIdList Like [PHYS_0001, PHYS_0002]
 * @param url Like http://digital-test.sbb.spk-berlin.de:83/
 * @param ppn Like PPN123456789
 * @param picturewidth Defines the rendered picture-width of every pic. The height is accordingly calculated to the width.
 */
DCPDF.buildImageBase64Arr = function (physIdList, url, ppn, picturewidth) {
    $.each(physIdList, function (key, physId) {
        DCPDF.base64images.push(url + ppn + ':' + physId + '/');
    });
    DCPDF.maxValueForProgress = physIdList.length;
    DCPDF.preload(ppn);
};

/**
 * Add metadata to the pdf (first page).
 * @param ppn Like PPN123456789
 * @returns {*} the doc Object with the metadata.
 */
DCPDF.createPdfWithCover = function (ppn) {
    var doc = new jsPDF("p", "mm", 'a4');
    // SUB Logo
    // TODO: Would be nicer to use SVG (not supported by jsPDF) or PNG (looks rugged)
    var logoDataUri = 'data:image/jpeg;base64,';

    var fontSize = 12;
    var gap = 40;
    var headlineFontSize = 16;
    var lineWidth = 190;
    var rowSpace = 9;
    var work = DCGlobals.getWork();
    var wrapSize = 160;
    var x = 20;
    var y = 50;

    doc.addImage(
        logoDataUri,
        20,
        20,
        109, // logo width
        10 // logo height
    );

    doc.setFontSize(headlineFontSize);
    var splittedHeadlineTitle = doc.splitTextToSize(work.title || '', wrapSize);
    for (var index = 0; index < splittedHeadlineTitle.length; index++) {
        if (index < 9) {
            doc.text(x, y, splittedHeadlineTitle[index]);
            if (index != splittedHeadlineTitle.length - 1) {
                y += rowSpace;
            }
        } else {
            doc.text(x, y, splittedHeadlineTitle[index] + " ...");
            break;
        }
    }
    doc.setFontSize(fontSize);

    y += rowSpace * .6;
    doc.setLineWidth(.5);
    doc.line(x, y, lineWidth, y);

    y += rowSpace * 2;
    doc.text(x, y, 'Autor:');
    doc.text(x + gap, y, work.author);

    y += rowSpace;
    doc.text(x, y, 'Erscheinungsjahr:');
    doc.text(x + gap, y, work.yearPublish);

    y += rowSpace;
    doc.text(x, y, 'Identifier:');
    doc.text(x + gap, y, ppn);

    y += rowSpace;
    doc.text(x, y, 'PURL:');
    doc.setTextColor(26, 55, 113);
    doc.text(x + gap, y, work.purl);
    doc.setTextColor(0, 0, 0);

    y += rowSpace;
    doc.text(x, y, 'Seiten:');
    doc.text(x + gap, y, DCPDF.internalOptions.physIdstart + '–' + DCPDF.internalOptions.physIdend); // That's an ndash.

    y += rowSpace;
    doc.text(x, y, 'Kontakt:');
    doc.text(x + gap, y, 'Niedersächsische Staats- und Universitätsbibliothek');
    y += rowSpace / 2;
    doc.text(x + gap, y, 'GDZ | Reproduktion');
    y += rowSpace / 2;
    doc.text(x + gap, y, '37070 Göttingen');
    y += rowSpace / 2;
    doc.text(x + gap, y, 'Germany');
    y += rowSpace / 2;
    doc.text(x + gap, y, 'E-Mail: gdz@sub.uni-goettingen.de');

    y += rowSpace;
    doc.setFontSize(headlineFontSize * .75);
    doc.text(x, y, doc.splitTextToSize(work.license, wrapSize));

    return doc;
};

/**
 * @summary Main function for the pdf-generation using jsPDF. DCPDF.base64data contains data-urls which will be inserted.
 * @param ppn like PPN1234567890
 */
DCPDF.buildPDF = function (ppn) {
    var doc = DCPDF.createPdfWithCover(ppn);
    $.each(DCPDF.base64data, function (key, item) {
        // TODO: Find a way to work with promise without accessing _detail
        ppiX = DCPDF.imagesInfos[key]._detail.ppi[0];
        ppiY = DCPDF.imagesInfos[key]._detail.ppi[1];
        var itemWidthInMM = item.width * 25.4 / ppiX;
        var itemHeightInMM = item.height * 25.4 / ppiY;
        doc.addPage(itemWidthInMM, itemHeightInMM);
        doc.addImage(
            item.dataURL,
            DCPDF.internalOptions.format,
            DCPDF.internalOptions.x,
            DCPDF.internalOptions.y,
            itemWidthInMM,
            itemHeightInMM
        );
    });

    try {
        doc.save(ppn + '.pdf');
        $('.export_cancel').hide();
        $('.export_reset').show();
    } catch (e) {
        console.log('error ' + e);
        DCPDF.showError('pdf_error_too_big');
    }
};

/**
 * @summary Function which is called from outside (template.js etc.). This function needs configuration-object like:
 * {
 *    url: "http://example.com/"
 *    width: 0, // of picutures
 *    height: 0, // of pictures
 *    x: 0, // position x
 *    y: 0, // position y
 *    format: 'JPEG', // picture format
 *    picturewidth: '1000', // picture width in px which are rendered from the ngcs (Content Server).
 *    physIdstart: 2,
 *    physIdend: 5
 * } and the PPN of the work.
 * @param ppn
 * @param physIdstart
 * @param physIdend
 */
DCPDF.generatePDF = function (ppn, physIdstart, physIdend) {
    DCPDF.isRunning = true;
    DCPDF.base64images = [];
    DCPDF.base64data = [];
    DCPDF.images = [];
    DCPDF.imagesInfos = [];
    DCPDF.maxValueForProgress = 1;

    DCPDF.setProgress(0);
    DCPDF.showProgress();

    DCPDF.calculatePDFGenerationStatistics(1, DCGlobals.getWork().structure.pages_length);

    DCPDF.internalOptions = {
        url: window.location.origin + '/image/',
        width: 0,
        height: 0,
        x: 0,
        y: 0,
        format: 'JPEG',
        picturewidth: '',
        physIdstart: physIdstart,
        physIdend: physIdend
    };
    var physIdList = DCPDF.turnPageNumbersIntophysIdArray(physIdstart, physIdend);
    DCPDF.buildImageBase64Arr(physIdList, DCPDF.internalOptions.url, ppn, DCPDF.internalOptions.picturewidth);
};

/**
 * @summary Helper function to build a physId from a string like "23" to "EADWERAEE423"
 * @param theNumberOfphysId Like 23
 * @returns {string} Like "EADWERAEE423"
 */
DCPDF.buildphysIds = function (theNumberOfphysId) {
    return $('.viewer_control.-page-select option[value=' + theNumberOfphysId+ ']').attr('data-id');
};

/**
 * @summary Turn page numbers like 20-23 to an array with physIds like [PHYS_0020, PHYS_0021, PHYS_0022, PHYS_0023].
 * @param scope First page number
 * @param scopeEnd Last page number
 * @returns {*}
 */
DCPDF.turnPageNumbersIntophysIdArray = function (scope, scopeEnd) {
    var outputArrayWithphysIds = [];
    if (typeof scopeEnd !== 'undefined' && scopeEnd) {
        if (parseInt(scopeEnd) > parseInt(scope)) {
            for (var i = parseInt(scope); i < parseInt(scopeEnd) + 1; ++i) {
                outputArrayWithphysIds.push(DCPDF.buildphysIds(i));
            }
        } else if (parseInt(scopeEnd) < parseInt(scope)) {
            console.log("Scope start is bigger than scope end or equal. Please fix your parameters.");
            return null;
        } else if (parseInt(scopeEnd) == parseInt(scope)) {
            outputArrayWithphysIds.push(DCPDF.buildphysIds(scope));
        }
    } else {
        for (var i = 1; i < parseInt(scope) + 1; i++) {
            outputArrayWithphysIds.push(DCPDF.buildphysIds(i));
        }
    }
    return outputArrayWithphysIds;
};

/**
 * @summary Calculate the size of a PDF and insert the result to the pdf-generation button.
 * @param pageStart First selected page
 * @param pageEnd Last selected page
 */
DCPDF.calculatePDFGenerationStatistics = function (pageStart, pageEnd) {
    if (pageStart != "" && pageStart != "undefined" && ! isNaN(pageStart) && pageEnd != "" && pageEnd != "undefined" && ! isNaN(pageEnd)) {
        pageEnd = parseInt(pageEnd);
        pageStart = parseInt(pageStart);
        var result = Math.abs(Math.abs(pageEnd) - Math.abs(pageStart)) + 1;
        if (! isNaN(result)) {
            var calculatedSize = 0;
            switch ($("input:radio[name='pdf-resolution']:checked").val()) {
                case "1000px":
                    calculatedSize = 0.21 * result;
                    break;
                case "original":
                    calculatedSize = 1.4 * result;
                    break;
                case "choose-free":
                    var chooseFreeValue = $("#pdf-resolution-input").val();
                    var factor = chooseFreeValue / 1000.0;
                    calculatedSize = 0.21 * factor * result;
                    break;
                default:
                    calculatedSize = 0;
            }
            calculatedSize = Math.round(calculatedSize);
            if (calculatedSize === 0) {
                calculatedSize = "> 1";
            }
            // TODO: Show PDF size on button
            // if (result === 1 && calculatedSize === -1) {
            //     $(".export_generate-pdf").text('pdf_button_onePage');
            // } else if (result === 1 && calculatedSize !== -1) {
            //     $(".export_generate-pdf").text('pdf_button_onePage_size'  + calculatedSize + " MB");
            // } else if (result !== 1 && calculatedSize === -1) {
            //     $(".export_generate-pdf").text('pdf_button_size_part1' + result + 'pdf_button_size_part2');
            // } else {
            //     $(".export_generate-pdf").text('pdf_button_size_part1' + result + 'pdf_button_size_part2' + " (ca. " + calculatedSize + " MB");
            // }
        }
    }
};
